    import axios from 'axios'
    export default {
        data() {
            return {
                feedItems: [],
                feedLodingError: '',
                feedCounter: 10,
                page: 1,
                url: 'http://fe-test.guardtime.com/documents',
                docValid: true,
                alert: true
            }
        },
        methods: {
            getFeed() {
                this.feedLodingError = '';
                axios.get(this.url + '?perPage=50&page=' + this.page)
                .then((res) => {
                    this.feedItems = this.feedItems.concat(res.data.data);
                })
                .catch(() => {
                    this.feedLodingError = 'Service unavailable.'
                })

                console.log(setTimeout(this.checkFeedItems, 1000))
            },
            checkFeedItems() {
                return this.feedItems.length < 1;
            },
            async validateSingleDoc(id) {
                let modes = ['validateChecksum', 'validateSchema', 'validateSignature'];
                let gotFalseFromResponse = false;
                let feedObjectById = what => this.feedItems.find(element => element.id === what);
                let docValid = true
                let error = false

                if(feedObjectById(id)) {
                    this.$set(feedObjectById(id), 'checksum',  '');
                    this.$set(feedObjectById(id), 'schema',  '');
                    this.$set(feedObjectById(id), 'signature',  '');
                    this.$set(feedObjectById(id), 'validationError',  false);

                    for(let i = 0; i < modes.length; i++) {
                        await axios.post(this.url + '/'+ id + '/' + modes[i])
                            .then((res) => {
                                if (!res.data.valid) {
                                    gotFalseFromResponse = true
                                }
                                if (i === 0) {
                                    this.$set(feedObjectById(id), 'checksum',  res.data.valid);
                                    console.log('here 1', modes[i], res.data.valid)
                                }
                                else if (i === 1) {
                                    this.$set(feedObjectById(id), 'schema',  res.data.valid);
                                    console.log('here 2', modes[i], res.data.valid)
                                }
                                else {
                                    this.$set(feedObjectById(id), 'signature',  res.data.valid);
                                    console.log('here 3', modes[i], res.data.valid)
                                }
                            })
                            .catch(() => {
                                error = true
                                this.$set(feedObjectById(id), 'validationError',  true);
                            })
                        if (gotFalseFromResponse === true) {
                            docValid = false
                            break;
                        }
                        gotFalseFromResponse = false
                    }
                    if(docValid && !error) {
                        this.$set(feedObjectById(id), 'valid',  'valid');
                    }
                    else if (error) {
                        this.$set(feedObjectById(id), 'valid',  '');
                        this.$set(feedObjectById(id), 'checksum',  '');
                        this.$set(feedObjectById(id), 'schema',  '');
                        this.$set(feedObjectById(id), 'signature',  '');
                    }
                    else {
                        this.$set(feedObjectById(id), 'valid',  'notvalid');
                    }
                }
                docValid = true
                error = false
            },
            infiniteScroll() {
                window.onscroll = () => {
                    let bottomOfWindow = document.documentElement.scrollTop + window.innerHeight === document.documentElement.offsetHeight;

                    if (bottomOfWindow) {
                        this.page+=1;
                        this.getFeed()
                        this.feedCounter+=10;
                    }
                };

            }
    },
    created() {
        this.getFeed();
    },
    mounted() {
        this.infiniteScroll();
    }
};
