    import axios from 'axios'
    export default {
        data() {
            return {
                feedItems: [],
                feedLoadingError: '',
                feedCounter: 5,
                perPage: 50,
                page: 1,
                url: 'http://fe-test.guardtime.com/documentsr',
                alert: true,
                getFeedLoader: false,
            }
        },
        methods: {
            showData(state) {
                this.getFeedLoader = true
                this.feedLoadingError = '';
                // Relaod btn will appear, if feed not loaded after 3 sec.
                // Should be used in dev mode only to speed up testing.
                setTimeout(this.error504Handling, 3000);

                axios.get(this.url + '?perPage=' + this.perPage + '&page=' + this.page)
                .then((res) => {
                    this.getFeedLoader = false
                    this.feedItems = this.feedItems.concat(res.data.data);
                })
                .catch((error) => {
                    if (state === true)  {
                        this.getFeedLoader = false
                        this.showData(false)
                        console.log(error.response.data.error.code)
                    }
                })
            },
            async validateSingleDoc(id) {
                let modes = ['validateChecksum', 'validateSchema', 'validateSignature'];
                let feedObjectById = what => this.feedItems.find(element => element.id === what);
                let docByID = feedObjectById(id)

                if(docByID) {
                    this.initValues(docByID)
                    this.resetValues(docByID)

                    for(let i = 0; i < modes.length; i++) {
                        this.$set(docByID, 'valid',  'validationInProgress');
                        await axios.post(this.url + '/'+ id + '/' + modes[i])
                            .then((res) => {
                                if (!res.data.valid) {
                                    this.$set(docByID, 'gotFalseFromResponse',  true);
                                }
                                if (modes[i] === 'validateChecksum') {
                                    this.$set(docByID, 'checksum',  res.data.valid);
                                }
                                else if (modes[i] === 'validateSchema') {
                                    this.$set(docByID, 'schema',  res.data.valid);
                                }
                                else {
                                    this.$set(docByID, 'signature',  res.data.valid);
                                }
                            })
                            .catch((error) => {
                                this.$set(docByID, 'gotErrorFromResponse',  true);
                                console.log(error.response.data.error.code)
                            })
                        if (docByID.gotFalseFromResponse) {
                            this.$set(docByID, 'docValid',  false);
                            break;
                        }
                        if(docByID.gotErrorFromResponse) {
                            break;
                        }
                    }
                    if (docByID.gotErrorFromResponse) {
                        this.resetValues(docByID)
                    }
                    else {
                        if (docByID.docValid) {
                            this.$set(docByID, 'valid',  'valid');
                        }
                        else {
                            this.$set(docByID, 'valid',  'notValid');
                        }
                    }
                }
            },
            infiniteScroll() {
                window.onscroll = () => {
                    let bottomOfWindow = document.documentElement.scrollTop + window.innerHeight === document.documentElement.offsetHeight;

                    if (bottomOfWindow) {
                        this.page+=1;
                        this.showData(true)
                        this.feedCounter+=this.perPage;
                    }
                };
            },
            error504Handling() {
                this.getFeedLoader = false
                if (this.feedItems.length < 1) {
                    this.feedLoadingError = 'Service unavailable, please reload'
                }
            },
            validateAllDocs() {
                let that = this;
                this.feedItems.forEach(function(item) {
                    that.validateSingleDoc(item.id);
                });
            },
            initValues(obj) {
                this.$set(obj, 'gotFalseFromResponse',  false);
                this.$set(obj, 'gotErrorFromResponse',  false);
                this.$set(obj, 'docValid',  true);
            },
            resetValues(obj) {
                this.$set(obj, 'checksum',  '');
                this.$set(obj, 'schema',  '');
                this.$set(obj, 'signature',  '');
                this.$set(obj, 'valid',  '');
            }
    },
    created() {
        this.showData(true);
    },
    mounted() {
        this.infiniteScroll();
    }
};
